	/**
	 * Engine support fallback to .tga or .dds
	 */
	private String getAlternateFileName(String fileName) {
		if (fileName.endsWith(".tga")) {
			return fileName.replace(".tga", ".dds");
		}
		return fileName.replace(".dds", ".tga");
	}
